mapper.select:
select * from (select temp.*,row_number() over (partition by temp.inpatientNum,temp.orgCode order by temp.idr ) as group_idx from(
select piir.id$ as idr, to_char(piir.admission_date, 'yyyy-mm-dd hh24:mi:ss') as serviceTime, piir.mpiid$ as mpiid, piir.ORG_CODE as orgCode, piir.ORG_NAME as orgName, piir.INPATIENT_NUM as inpatientNum, piir.sex_code as sexCode, piir.INCLINIC_DOCTOR_NAME as doctorName, piir.pname as pname, piir.idcard_num as idcard, piir.idcard_num as idcardNum, piir.dep_code as deptCode, piir.dep_name as deptName, tep.* from PT_IP_IN_REG piir left join( select * from (select rp.ID$ as idq, '出院小结' as name, 'HB005' as metaSetCode, rp.ORG_CODE as orgCodeTep, rp.INPATIENT_NUM as inpatientNumTep, rp.ATTENDING_DOCTOR_CODE as serviceDoctorCode, to_char(rp.CREATED_AT, 'yyyy-mm-dd hh24:mi:ss') as createdAt, rp.CASE_HISTORY_ID as caseHistoryId, rp.HEALTH_CARD_NUM as healthCardNum, rp.AREA_NAME as areaName, rp.WARD_NUM as wardNum, rp.SICKBED_NUM as sickbedNum, rp.IDCARD_TYPE as idcardType, rp.OTHER_ID_NUM as otherIdNum, rp.AGE_YEARS as ageYears, rp.AGE_MONTHS as ageMonths, rp.AGE_DAYS as ageDays, rp.AGE_HOURS as ageHours, rp.MARITAL_STATUS_CODE as maritalStatusCode, rp.MARITAL_STATUS_NAME as maritalStatusName, rp.OCCUPATION_CODE as occupationCode, rp.OCCUPATION_NAME as occupationName, rp.TEL as tel, rp.ADDRESS_TYPE_CODE as addressTypeCode, rp.PROVINCE as province, rp.CITY as city, rp.COUNTY as county, rp.TOWN as town, rp.VILLAGE as village, rp.HOUSE_NUM as houseNum, rp.POSTAL_CODE as postalCode, rp.LINKMAN_NAME as linkmanName, rp.LINKMAN_TEL as linkmanTel, to_char(rp.ADMISSION_DATE, 'yyyy-mm-dd hh24:mi:ss') as admissionDate, to_char(rp.DISCHARGE_DATE, 'yyyy-mm-dd hh24:mi:ss') as dischargeDate, rp.INPATIENT_DAY as inpatientDay, rp.RESIDENT_DOCTOR_CODE as residentDoctorCode, rp.RESIDENT_DOCTOR_NAME as residentDoctorName, rp.ATTENDING_DOCTOR_CODE as attendingDoctorCode, rp.SUPERIOR_DOCTOR_NAME as superiorDoctorName, rp.ADMISSION_SITUATION as admissionSituation, rp.TREAT_RESULT_CODE as treatResultCode, rp.TREAT_PROCESS as treatProcess, rp.DISCHARGE_SITUATION as dischargeSituation, rp.DISCHARGE_SIGNS as dischargeSigns, rp.ASSIST_EXAM_RESULT as assistExamResult, rp.IN_DISCHARGE_SIGNS as inDischargeSigns, rp.INSPECT_CONSULTATION as inspectConsultation, rp.SPECIAL_EXAM as specialExam, rp.DISEASE_OUTCOME_CODE as diseaseOutcomeCode, rp.COMPLICATION as complication, rp.DISCHARGE_ORDER as dischargeOrder, rp.FLAG_FOUR_DIAGNOSIS as flagFourDiagnosis, rp.FOUR_DIAGNOSIS as fourDiagnosis, rp.TREATMENT_METHOD as treatmentMethod, rp.TCM_DECOCTION_METHOD as tcmDecoctionMethod, rp.MEDICATION_METHOD as medicationMethod ,row_number() over (partition by inpatient_num,org_code order by mpiid$ ) as rownum1 from PT_EMRI_DISCHARGE rp) where rownum1 = 1 ) tep on tep.inpatientNumTep=piir.inpatient_num and tep.orgCodeTep=piir.org_code where 1=1 and
piir.id$= ${item}
) temp) temprow_number where group_idx=1
